const fs = require('fs');

// 200x200の青い PNG 画像を base64 から生成
const base64Image = '';

const buffer = Buffer.from(base64Image, 'base64');
fs.writeFileSync('evidence-screenshot.png', buffer);

console.log('✅ テスト用エビデンス画像を生成しました: evidence-screenshot.png');
console.log('   サイズ: 200x200px');
console.log('   色: 青（#1890ff）');
